#ifndef TEST_TEST_BIT_SET_H_
#define TEST_TEST_BIT_SET_H_

#include <iostream>

#include "utils/bit-set.h"

namespace ncr {
void TestBitSet() {
	bitset b;
	// 8 bits
	b.add(1);
	b.add(1);
	b.add(0);
	b.add(1);
	b.add(0);
	b.add(0);
	b.add(1);
	b.add(1);
	// 8 bits
	b.add(0);
	b.add(0);
	b.add(0);
	b.add(1);
	b.add(1);
	b.add(0);
	b.add(1);
	b.add(1);
	// 1 bit
	b.add(1);

	std::cout << b << std::endl;
	auto s = b.to_string();
	b.from_string(s, b.size());
	std::cout << b << std::endl;
	std::cout << "Requires " << s.size() << " byte(s)" << std::endl;

	b.from_full_string(
			"");
	std::cout << b << std::endl << std::endl;
	s = b.to_string();
	b.clear();
	b.from_string(s, b.size());
	std::cout << b << std::endl;
}
}

#endif /* TEST_TEST_BIT_SET_H_ */
